import React from 'react';
import { useThree } from 'react-three-fiber';
import { PerspectiveCamera } from 'drei'; // Sets default camera to a standard camera adjusted so that 1 unit == 1 px
// Use to make 3D planes appear as 2D HTML images

function FlatCamera() {
  const _useThree = useThree(),
        camera = _useThree.camera;

  const perspective = 800;
  const fov = 180 * (2 * Math.atan(window.innerHeight / 2 / perspective)) / Math.PI;
  camera.fov = fov;
  camera.position.z = perspective;
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.near = 1;
  camera.far = 1000;
  camera.updateProjectionMatrix();
}

export default FlatCamera;
