import React, { useRef } from 'react'
import { Color } from 'three'
import { useThree } from 'react-three-fiber'

function Bar(props) {
  const { viewport } = useThree()

  // random x position across screen
  function randomPosition() {
    return Math.floor(-viewport.width / 2 + Math.random() * (viewport.width / 2 + 1 + viewport.width / 2))
  }

  return (
    <mesh position={[props.bar.positionX, 0, 0]}>
      <planeBufferGeometry attach="geometry" args={[props.bar.width, props.bar.height]} />
      <meshBasicMaterial attach="material" color={props.bar.color} />
    </mesh>
  )
}

export default Bar
