import React, { useState, useRef } from 'react'
import { useFrame, useThree } from 'react-three-fiber'
import * as THREE from 'three'

function Storm(props) {
  const smoke = useRef()
  const wire = useRef()

  const {
    viewport: { width, height }
  } = useThree()

  const radius = Math.max(width / 4, height / 4)

  const wireframe = new THREE.WireframeGeometry(new THREE.IcosahedronBufferGeometry(radius + 0.5, 3))

  return (
    <group>
      <mesh ref={smoke}>
        <icosahedronBufferGeometry args={[radius, 5]} attach="geometry" />
        <meshStandardMaterial attach="material" color={'pink'} />
      </mesh>

      <lineSegments>
        <wireframeGeometry attach="geometry">
          <icosahedronBufferGeometry attach="geometry" args={[radius + 0.5, 3]} />
        </wireframeGeometry>
        <lineBasicMaterial color="black" />
      </lineSegments>
    </group>
  )
}

export default Storm
