import React, { useRef } from 'react'
import { Color } from 'three'
import { useThree } from 'react-three-fiber'
import Bar from './Bar'

function Bars(props) {
  const inner = useRef()
  const bars = useRef()

  const { viewport } = useThree()

  // random x position to start at
  function randomPosition() {
    return Math.floor(-viewport.width / 2 + Math.random() * (viewport.width / 2 + 1 + viewport.width / 2))
  }

  const barsStore = [
    {
      id: 'bar1',
      width: viewport.width / 10,
      height: viewport.height,
      color: new Color('#F00000'),
      positionX: randomPosition()
    },
    {
      id: 'bar2',
      width: viewport.width / 5,
      height: viewport.height,
      color: new Color('#600000'),
      positionX: randomPosition()
    }
  ]

  return (
    <group ref={bars}>
      {barsStore.map((bar, index) => (
        <Bar key={bar.id} bar={bar} />
      ))}
    </group>
  )
}

export default Bars
