import React from 'react'
import { useThree } from 'react-three-fiber'

import '../materials/GradientOpacityMaterial'

function AngledWipe(props) {
  const { viewport } = useThree()

  return (
    <group {...props}>
      <mesh>
        <planeBufferGeometry attach="geometry" args={[viewport.width, viewport.height]} />
        <gradientOpacityMaterial attach="material" uColor={props.color} toneMapped={false} transparent />
      </mesh>
    </group>
  )
}

export default AngledWipe
