function lerp(v0, v1, t) {
    return v0*(1-t)+v1*t
}

function map(value, min1, max1, min2, max2, clampResult) {
    var returnvalue = ((value - min1) / (max1 - min1)) * (max2 - min2) + min2
    if (clampResult) {
        return clamp(returnvalue, min2, max2)
    } else {
        return returnvalue
    }
}

module.exports = lerp
