import React, { useState, useRef } from 'react'
import { useFrame, useThree } from 'react-three-fiber'
import * as THREE from 'three'
import lerp from 'lerp'
import state from '../../store'

import '../materials/StormMaterial'

function Storm(props) {
  const smoke = useRef()
  const wire = useRef()
  const smokeMaterial = useRef()

  const {
    viewport: { width, height }
  } = useThree()

  const radius = Math.max(width / 4, height / 4)

  const wireframeGeometry = new THREE.WireframeGeometry(new THREE.IcosahedronBufferGeometry(radius * 1.1, 3))
  const wireframeMaterial = new THREE.LineBasicMaterial({ color: 'black' })

  let last = state.top.current

  useFrame(() => {
    const { top } = state
    const scrollSpeed = top.current - last
    smokeMaterial.current.time = smokeMaterial.current.LowFreqAmp = lerp(smokeMaterial.current.LowFreqAmp, Math.abs(scrollSpeed) / 150, 0.1)
    smokeMaterial.current.MidFreqAmp = lerp(smokeMaterial.current.MidFreqAmp, Math.abs(scrollSpeed) / 150, 0.1)
    smokeMaterial.current.HighFreqAmp = lerp(smokeMaterial.current.HighFreqAmp, Math.abs(scrollSpeed) / 150, 0.1)

    last = top.current
  })

  return (
    <group>
      <mesh ref={smoke}>
        <icosahedronBufferGeometry args={[radius, 5]} attach="geometry" />
        <customMaterial ref={smokeMaterial} attach="material" />
      </mesh>

      <lineSegments args={[wireframeGeometry, wireframeMaterial]} />
    </group>
  )
}

export default Storm
