import React, { useRef } from 'react'
import { Color } from 'three'
import { useThree } from 'react-three-fiber'

function Bars(props) {
  const inner = useRef()
  const bars = useRef()

  const { viewport } = useThree()

  const barsStore = {
    bar1: {
      width: viewport.width / 10,
      height: viewport.height,
      color: new Color('#F00000')
    },
    bar2: {
      width: viewport.width / 5,
      height: viewport.height,
      color: new Color('#600000')
    }
  }

  return (
    <group ref={bars}>
      {barsStore.map((bar, index) => (
        <h1>{bar.width}</h1>
      ))}
      <group ref={inner} {...props}>
        <mesh>
          <planeBufferGeometry attach="geometry" args={[2, 0.5]} />
          <meshBasicMaterial attach="material" color="#FFEDDD" toneMapped={false} />
        </mesh>
        <mesh position={[0, -0.625, 0]}>
          <planeBufferGeometry attach="geometry" args={[0.5, 0.75]} />
          <meshBasicMaterial attach="material" color="#FFEDDD" toneMapped={false} />
        </mesh>
        <mesh position={[0, 0.625, 0]}>
          <planeBufferGeometry attach="geometry" args={[0.5, 0.75]} />
          <meshBasicMaterial attach="material" color="#FFEDDD" toneMapped={false} />
        </mesh>
      </group>
    </group>
  )
}

export default Bars
