import { useMemo, useEffect } from 'react'
import { useThree, useFrame } from 'react-three-fiber'
import { EffectComposer, EffectPass, NoiseEffect, RenderPass } from 'postprocessing'

export default function Effects(props) {
  const { gl, scene, size } = useThree()
  const camera = props.currentCamera

  const composer = useMemo(() => {
    const composer = new EffectComposer(gl)
    composer.addPass(new RenderPass(scene, camera))
    const noiseEffect = new NoiseEffect()
    const effectPass = new EffectPass(camera, noiseEffect)
    effectPass.renderToScreen = true
    composer.addPass(effectPass)
    return composer
  }, [])
  useEffect(() => void composer.setSize(size.width, size.height), [size])
  return useFrame((_, delta) => composer.render(delta), 1)
}
