import React, { useRef, useState } from 'react'
import { Color } from 'three'
import { useThree, useFrame } from 'react-three-fiber'
import lerp from 'lerp'

function Bar(props) {
  const bar = useRef()
  const { viewport } = useThree()
  const [minWidth, maxWidth] = [-viewport.width / 2, viewport.width / 2]
  const [minInterval, maxInterval] = [1000, 5000]
  const [minOpacity, maxOpacity] = [0.0, 0.8]

  const [posX, setPosX] = useState(0)
  const [opacity, setOpacity] = useState(0)

  // returns random int between lower and upper bounds
  function random(lowerBound, upperBound) {
    return Math.floor(lowerBound + Math.random() * (upperBound + 1 - lowerBound))
  }

  // set new random position for the bar every 1 - 5 seconds
  setTimeout(() => setPosX(random(minWidth, maxWidth)), random(minInterval, maxInterval))

  // lerp bar to its new position
  useFrame(() => {
    bar.current.position.x = lerp(bar.current.position.x, posX, 0.01)
  })

  return (
    <mesh ref={bar} position={[0, 0, 0]}>
      <planeBufferGeometry attach="geometry" args={[props.bar.width, props.bar.height]} />
      <meshBasicMaterial attach="material" color={props.bar.color} opacity={opacity} transparent={true} />
    </mesh>
  )
}

export default Bar
