import React from 'react'
import * as THREE from 'three'

// A 3D plane representing an HTML image element with CSS positioning

function ImagePlane({ children }) {
  const image = children[0]
  console.log(image)

  const loader = new THREE.TextureLoader()

  const imageTexture = loader.load(image.dataset.src)
  const sizes = new THREE.Vector2(0, 0)
  const offset = new THREE.Vector2(0, 0)

  const { width, height, top, left } = image.getBoundingClientRect()

  sizes.set(width, height)
  offset.set(left - window.innerWidth / 2 + width / 2, -top + window.innerHeight / 2 - height / 2)

  return (
    <mesh position={[offset.x, offset.y, 0]} scale={[sizes.x, sizes.y, 1]}>
      <planeBufferGeometry attach="geometry" args={[1, 1, 1, 1]} />
      <meshBasicMaterial attach="material" map={imageTexture} />
    </mesh>
  )
}

export default ImagePlane
