import React, { useRef } from 'react'
import { Color } from 'three'
import { useThree } from 'react-three-fiber'

function Bars(props) {
  const inner = useRef()
  const bars = useRef()

  const { viewport } = useThree()

  const barsStore = [
    {
      id: 'bar1',
      width: viewport.width / 10,
      height: viewport.height,
      color: new Color('#F00000')
    },
    {
      id: 'bar2',
      width: viewport.width / 5,
      height: viewport.height,
      color: new Color('#600000')
    }
  ]

  return (
    <group ref={bars}>
      {barsStore.map((bar, index) => (
        <mesh key={bar.id}>
          <planeBufferGeometry attach="geometry" args={[bar.width, bar.height]} />
          <meshBasicMaterial attach="material" color={bar.color} />
        </mesh>
      ))}
    </group>
  )
}

export default Bars
