import React, { useRef } from 'react'
import { useThree } from 'react-three-fiber'
import { HTML } from 'drei'

// drei component tying html elements to any object in a scene
// portal: target container

function HtmlContent({ className, style, children, portal }) {
  const { size } = useThree()
  return (
    <HTML
      portal={portal}
      style={{
        position: 'absolute',
        top: -size.height / 2,
        left: -size.width / 2,
        width: size.width,
        height: size.height
      }}>
      <div className={className} style={style}>
        {children}
      </div>
    </HTML>
  )
}

function HtmlWithImage({ className, style, children, portal }) {
  const { size } = useThree()
  const imagePlane = useRef()

  const image = document.getElementsByClassName('.image-plane')[0]
  console.log(image)
  console.log(image.dataset.src)

  const loader = new THREE.TextureLoader()

  const imageTexture = loader.load(image.current.props.src)
  const sizes = new THREE.Vector2(0, 0)
  const offset = new THREE.Vector2(0, 0)

  const { width, height, top, left } = image.current.getBoundingClientRect()

  sizes.set(width, height)
  offset.set(left - window.innerWidth / 2 + width / 2, -top + window.innerHeight / 2 - height / 2)

  return (
    <HTML
      portal={portal}
      style={{
        position: 'absolute',
        top: -size.height / 2,
        left: -size.width / 2,
        width: size.width,
        height: size.height
      }}>
      <div className={className} style={style}>
        {children}
      </div>
    </HTML>
  )
}

export { HtmlContent, HtmlWithImage }
