import React, { useRef, useState } from 'react'
import { Color } from 'three'
import { useThree, useFrame } from 'react-three-fiber'
import lerp from 'lerp'

function Bar(props) {
  const bar = useRef()
  const { viewport } = useThree()
  const [minWidth, maxWidth] = [-viewport.width / 2, viewport.width / 2]
  const [minInterval, maxInterval] = [1000, 5000]
  const [minOpacity, maxOpacity] = [0.0, 0.8]

  const [posX, setPosX] = useState(randomPosition())

  // random x position across screen
  function randomPosition() {
    console.log(Math.floor(minWidth + Math.random() * (maxWidth + 1 - minWidth)))

    return Math.floor(minWidth + Math.random() * (maxWidth + 1 - minWidth))
  }

  function randomInterval() {
    return Math.floor(minInterval + Math.random() * (maxInterval + 1 - minInterval))
  }

  // set new random position for the bar every 1 - 5 seconds
  setTimeout(() => setPosX(randomPosition()), randomInterval())

  // lerp bar to its new position
  useFrame(() => {
    bar.current.position.x = lerp(bar.current.position.x, posX, 0.01)
  })

  return (
    <mesh ref={bar} position={[0, 0, 0]}>
      <planeBufferGeometry attach="geometry" args={[props.bar.width, props.bar.height]} />
      <meshBasicMaterial attach="material" color={props.bar.color} opacity={0.5} transparent={true} />
    </mesh>
  )
}

export default Bar
